mapper.select:
SELECT A.* FROM( SELECT ROWNUM RNUM , MEM_ID, MEM_NAME, MEM_HP , MEM_MAIL, MEM_ADD1, MEM_MILEAGE , MEM_BIR , ( SELECT COUNT(DISTINCT CART_PROD) FROM CART WHERE CART_MEMBER = MEM_ID ) PROD_COUNT FROM MEMBER
) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:
